package com.team.util;import java.io.BufferedReader;import java.io.BufferedWriter;import java.io.File;import java.io.FileReader;import java.io.FileWriter;import java.io.IOException;public class FileUtil {	/**	 * 	 * @param sourceFile	 * @param targetFile	 * @throws Exception	 */	public static void copyFile(String sourceFilePath, String targetFilePath)			throws Exception {		copyFile(new File(sourceFilePath), new File(targetFilePath));	}	public static void copyFile(File sourceFile, File targetFile)			throws Exception {		BufferedReader reader = null;		BufferedWriter writer = null;		try {			reader = new BufferedReader(new FileReader(sourceFile));			writer = new BufferedWriter(new FileWriter(targetFile));			String s;			while ((s = reader.readLine()) != null) {				writer.write(s + "\n");			}		} catch (Exception e) {			e.printStackTrace();		} finally {			writer.flush();			reader.close();			writer.close();		}	}	public static void copyDirectory(String sourceDir, String targetDir) throws Exception {		(new File(targetDir)).mkdirs();		File[] file = (new File(sourceDir)).listFiles();		for(File temp : file){			if(temp.isFile()){				File sourceFile = temp;				File targetFile = new File(new File(targetDir).getAbsolutePath() + File.separator + sourceFile.getName());				copyFile(sourceFile,targetFile);			}else if(temp.isDirectory()){				String subSource = sourceDir + File.separator + temp.getName();				String subTarget = targetDir + File.separator + temp.getName();				copyDirectory(subSource,subTarget);			}		}	}}