package com.team.util;import java.io.BufferedReader;import java.io.File;import java.io.FileReader;import java.util.ArrayList;import java.util.List;public class TagsUtil {	private static List<String> tags = new ArrayList<String>();	public static List<String> tags(String tagFilePath) throws Exception{		if(tags.size() == 0){			BufferedReader reader = new BufferedReader(new FileReader(new File(tagFilePath)));			String tag;			while ((tag = reader.readLine()) != null) {				tags.add(tag);			}		}		return tags;	}		}